import React, { Component, useState } from 'react';
import { FaStar } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import axios from 'axios';
import moment from 'moment';
import decode from 'jwt-decode';
import Comment from '../../Comment/Comment';
// import StarRating from 'react-star-rating';
import StarRating from '../UserProfiles/Rating';

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      rating: '',
      user: '',
      data: [],
    };
  }
  componentDidMount() {
    const ratingGiver = decode(localStorage.getItem('token'))._id;
    const ratingReciever = this.props.location.state.record._id;

    axios
      .get(
        `http://localhost:5000/api/rating/fetchrating/${ratingGiver}/${ratingReciever}`
      )
      .then((res) => {
        console.log(res.data.rating);
        this.setState({ rating: res.data.rating });
      });
    axios
      .get(
        `http://localhost:5000/api/users/display/comment/${this.props.location.state.record._id}`
      )
      .then((res) => {
        console.log(res.data);
        this.setState({
          data: res.data,
        });
        console.log('Commenter', this.state.data.firstName);
      })
      .catch((err) => {
        console.log(err);
      });
  }

  render() {
    let { data, isShowingAlert } = this.state;
    return (
      <div className="container">
        <div className="row">
          {/* <h2
            style={{ textAlign: 'center', padding: '30px', fontWeight: 'bold' }}
          >
            Mentor Profile
          </h2> */}

          <div className="col-4">
            <div
              className="card shadow-lg p-3 mb-5 bg-white rounded"
              style={{
                width: '18rem',
                padding: '20px',
                margin: '10px 0px',

                // boxShadow: '20px ',
              }}
            >
              {this.state.rating ? (
                <div className="card-body">
                  <h6
                    className="card-title"
                    style={{ fontWeight: 'bold', padding: '10px 0px' }}
                  >
                    {this.props.location.state.record.firstName.toUpperCase()}
                    &nbsp;&nbsp;
                    {this.props.location.state.record.lastName.toUpperCase()}
                  </h6>
                  <h3></h3>
                  <div>
                    <StarRating
                      rating={this.state.rating}
                      mentorId={this.props.location.state.record._id}
                    />
                  </div>
                  <div>
                    You rated:
                    {this.state.rating}/5
                  </div>

                  <h6 className="card-title">
                    {this.props.location.state.record.stream}
                  </h6>
                  <h6 className="card-title">
